a:application.a:script:
		SupportedModes = {
			"application/javascript": "js",
			"application/json": "js",
			"text/css": "css",
			"text/html": "html",
			"application/xhtml+xml": "html",
			"application/xml": "xml"
		}

		function getSyntax(xmlNode) {
             var mime = xmlNode.getAttribute('contenttype').split(";")[0];
             return (SupportedModes[mime] || "text");
		}
