a:application.a:script:
		SupportedModes = {
			"application/javascript": "JavaScript",
			"application/json": "JavaScript",
			"text/css": "Css",
			"text/html": "Html",
			"application/xhtml+xml": "Html",
			"application/xml": "Xml"
		}

		function getSyntax(type) {
             var mime = type.split(";")[0];
             return (SupportedModes[mime] || "Text");
		}
